INSERT INTO news (title, summary, content, cover_url, author, category, publish_time, view_count) VALUES 
('中超揭幕战：上海海港 3-1 武汉三镇', '武磊梅开二度，海港 3-1 战胜三镇。', '今晚中超揭幕战在浦东足球场上演，上海海港凭借武磊的梅开二度，以 3-1 战胜武汉三镇，取得新赛季开门红。', 'https://img1.baidu.com/it/u=189031102,3060505118&fm=253&fmt=auto&app=138&f=JPEG?w=800&h=500', '体坛周报', '中超', NOW(), 1250), 
('欧冠前瞻：皇马 vs 曼城 天王山之战', '皇马主场迎战曼城，欧冠巅峰对决。', '欧冠四分之一决赛首回合即将开打，皇家马德里将在主场迎战曼城。这场被誉为提前上演的决赛吸引了全球目光。', 'https://img0.baidu.com/it/u=2523214811,1015637213&fm=253&fmt=auto&app=138&f=JPEG?w=889&h=500', '足球部落', '欧冠', NOW(), 5600), 
('国足公布新一期大名单：阿兰回归', '阿兰重返国足，新一期集训名单出炉。', '中国足协今日公布了国家队新一期集训名单，入籍球员阿兰时隔多日重新入选，武磊、张玉宁等主力悉数在列。', 'https://img2.baidu.com/it/u=1130663715,1383921356&fm=253&fmt=auto&app=138&f=JPEG?w=500&h=333', '新浪体育', '国足', NOW(), 3400);